    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double peso, altura , calc;
        
        
        peso= Double.parseDouble(p.getText());
        altura=Double.parseDouble(a.getText());
        
        calc = peso/(altura* peso);
        if(calc <18.5){
           l.setText("Abaixo do peso");
          t.setText( Double.toString(calc ));
        }
                if(calc >=18.6 && calc <=24.9 ){
           l.setText("Peso ideal");
          t.setText( Double.toString(calc ));
        }
        
              if(calc >=25 && calc <=29.9 ){
           l.setText("levemente acima do peso");
          t.setText( Double.toString(calc ));
        }     
          if(calc >=30 && calc <=34.9 ){
           l.setText("obesidade grau 1");
          t.setText( Double.toString(calc ));
        } 
          if(calc >=35 && calc <=39.9 ){
           l.setText("obesidade grau 2(severa)");
          t.setText( Double.toString(calc ));
        }     
         if(calc >40){
           l.setText("obesidade grau 3(m√≥rbida)");
          t.setText( Double.toString(calc ));
        } 
         p.setText("");
         a.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

